import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ChatComponent = class ChatComponent {
    constructor(service, scroller) {
        this.service = service;
        this.scroller = scroller;
        this.autoScroll = false;
        this.blocked = false;
        this.mensagens = [];
        this.usuario = true;
        this.sliceOptions = {
            start: 0,
            end: 300,
            default: 300
        };
    }
    ngOnInit() { }
    onExpandText(evt) {
        this.sliceOptions.end = this.sliceOptions.end ? undefined : this.sliceOptions.default;
    }
    onSubmit() {
        return this.valor != null ? this.valor : null;
    }
    async preencherMensagem(response, link) {
        this.mensagens.push({
            mensagem: String(response),
            usuario: false,
            error: false,
            url: String(link)
        });
    }
    async enviarDado() {
        var element = document.getElementById("target");
        if (this.valor) {
            this.mensagens.push({
                mensagem: String(this.valor),
                usuario: true,
                error: false,
                url: "",
            });
            if (!this.blocked) {
                const response = await this.service.SendMessage(this.valor);
                this.valor = "";
                this.preencherMensagem(response.data, "");
                element.scrollIntoView(false);
                if (response.tag == "query") {
                    this.blocked = true;
                }
            }
            else {
                const response = await this.service.SendQuery(this.valor);
                this.valor = "";
                this.preencherMensagem("I found these courses that may suit your query:", "");
                element.scrollIntoView(false);
                for (const message of response.data) {
                    console.log(message);
                    this.preencherMensagem("The course " + message.name + ", rated " + message.rated + " is offered by " + message.host + "and has difficulty level " + message.difficultie + ".\n\n" + message.description + "\n\n In this course you will learn: " + message.skill, message.url);
                }
                this.blocked = false;
            }
        }
        else {
            this.preencherMensagemVazia();
        }
    }
    preencherMensagemVazia() {
        this.mensagens.push({
            mensagem: String('Favor Digitar uma Mensagem...'),
            usuario: false,
            error: true,
            url: "",
        });
    }
};
ChatComponent = __decorate([
    Component({
        selector: 'app-chat',
        templateUrl: './chat.component.html',
        styleUrls: ['./chat.component.css']
    })
], ChatComponent);
export { ChatComponent };
