    constructor(service, dom, cd) {
        this.service = service;
        this.dom = dom;
        this.cd = cd;
        this.blocked = false;
        this.isOpen = false;
        this.mensagens = [];
        this.usuario = true;
        this.chunks = [];
        this.audioFiles = [];
        this.canRecord = true;
        this.sliceOptions = {
            start: 0,
            end: 300,
            default: 300
        };
        this.mensagens.push({
            mensagem: String('Hi, I am your support agent. How can I assist you?'),
            usuario: false,
            error: true,
            audio: false,
            url: "",
        });
        this.sugestion();
    }
    ngOnInit() {
        navigator.mediaDevices.getUserMedia({ audio: true }, (stream) => {
            const options = {
                audioBitsPerSecond: 48000,
