                    usuario: true,
                    error: false,
                    audio: true,
                    url: true
                });
                this.audioFiles.push(this.dom.bypassSecurityTrustUrl(audioURL));
                console.log('recorder stopped');
                this.cd.detectChanges();
            };
            this.mediaRecorder.ondataavailable = (e) => {
                this.chunks.push(e.data);
            };
        }, () => {
            alert('Error capturing audio.');
        });
    }
