        return this.valor != null ? this.valor : null;
    }
    async preencherMensagem(response, link) {
        this.mensagens.push({
            mensagem: response,
            usuario: false,
            error: false,
            audio: false,
            url: String(link)
        });
    }
    async enviarDado() {
        var element = document.getElementById("target");
        if (this.valor) {
            this.mensagens.push({
                mensagem: String(this.valor),
                usuario: true,
                error: false,
                audio: false,
                url: "",
            });
            if (!this.blocked) {
