import { __decorate } from "tslib";
import { Component, ViewChild } from '@angular/core';
let ChatComponent = class ChatComponent {
    constructor(service, scroller, translate) {
        this.service = service;
        this.scroller = scroller;
        this.translate = translate;
        this.autoScroll = false;
        this.blocked = false;
        this.isOpen = false;
        this.mensagens = [];
        this.usuario = true;
        this.sliceOptions = {
            start: 0,
            end: 300,
            default: 300
        };
        translate.setDefaultLang('en');
        translate.use('en');
        this.mensagens.push({
            mensagem: String("Hello, I'm your support agent. In what language can I help you?"),
            usuario: false,
            error: true,
            url: "",
        });
        this.mensagens.push({
            mensagem: String("Olá, sou seu agente de suporte. Em que linguagem posso ajudá-lo?"),
            usuario: false,
            error: true,
            url: "",
        });
        this.mensagens.push({
            mensagem: String("Hola, soy tu agente de soporte. ¿En qué idioma puedo ayudarte?"),
            usuario: false,
            error: true,
            url: "",
        });
    }
    useLanguage(language) {
        this.translate.use(language);
    }
    ngOnInit() { }
    onExpandText(evt) {
        this.sliceOptions.end = this.sliceOptions.end ? undefined : this.sliceOptions.default;
    }
    openSupportPopup() {
        this.isOpen = !this.isOpen;
    }
    onSubmit() {
        return this.valor != null ? this.valor : null;
    }
    async preencherMensagem(response, link) {
        this.mensagens.push({
            mensagem: String(response),
            usuario: false,
            error: false,
            url: String(link)
        });
    }
    async enviarDado() {
        var element = document.getElementById("target");
        if (this.valor) {
            this.valor.trim().toLowerCase();
            if (this.valor.match("portugues") || this.valor.match("português") || this.valor.match("portuguese") || this.valor.match("portugués")) {
                this.language = "portugues";
            }
            else if (this.valor.match("ingles") || this.valor.match("english") || this.valor.match("english") || this.valor.match("inglês") || this.valor.match("inglés")) {
                this.language = "ingles";
            }
            else if (this.valor.match("espanhol") || this.valor.match("spanish") || this.valor.match("español")) {
                this.language = "espanhol";
            }
            else {
                this.language = this.language;
            }
            this.mensagens.push({
                mensagem: String(this.valor),
                usuario: true,
                error: false,
                url: "",
            });
            if (this.language) {
                if (!this.blocked) {
                    const response = await this.service.SendMessage(this.valor);
                    this.valor = "";
                    this.preencherMensagem(response.data, "");
                    if (response.tag == "query") {
                        this.blocked = true;
                    }
                }
                else {
                    const response = await this.service.SendQuery(this.valor);
                    this.preencherCursos(response);
                    this.blocked = false;
                }
            }
            else {
                this.mensagens.push({
                    mensagem: String('Por favor, digite uma linguagem de atendimento válida. Temos suporte em Inglês, Português e Espanhol.'),
                    usuario: false,
                    error: true,
                    url: "",
                });
                this.mensagens.push({
                    mensagem: String('Please enter a valid service language. We have support in English, Portuguese and Spanish.'),
                    usuario: false,
                    error: true,
                    url: "",
                });
                this.mensagens.push({
                    mensagem: String('Introduzca un idioma de servicio válido. Tenemos soporte en Inglés, Portugués y Español.'),
                    usuario: false,
                    error: true,
                    url: "",
                });
            }
        }
        else {
            this.preencherMensagemVazia();
        }
    }
    preencherMensagemVazia() {
        this.mensagens.push({
            mensagem: String('AAAAAAAAAAAA'),
            usuario: false,
            error: true,
            url: "",
        });
    }
    preencherCursos(response) {
        var course = "";
        var rated = "";
        var offered = "";
        var difficulty = "";
        var learn = "";
        if (this.language == "portugues") {
            course = "O curso ";
            rated = ", classificado ";
            offered = " é oferecido por ";
            difficulty = "e tem nível de dificuldade ";
            learn = "\n\n Neste curso você aprenderá: ";
            this.preencherMensagem("Encontrei estes cursos que podem atender sua consulta:", "");
        }
        else if (this.language == "ingles") {
            course = "The course ";
            rated = ", rated ";
            offered = " is offered by ";
            difficulty = "and has difficulty level ";
            learn = "\n\n In this course you will learn: ";
            this.preencherMensagem("I found these courses that may suit your query:", "");
        }
        else {
            course = "El curso ";
            rated = ", clasificado ";
            offered = " es ofrecido por ";
            difficulty = "y tiene nivel de dificultad ";
            learn = "\n\n En este curso aprenderás: ";
            this.preencherMensagem("Encontré estos cursos que pueden adaptarse a tu consulta:", "");
        }
        for (const message of response.data) {
            this.preencherMensagem(course + message.name + rated + message.rated + offered + message.host + difficulty + message.difficultie + ".\n\n" + message.description + learn + message.skill, message.url);
        }
    }
};
__decorate([
    ViewChild('scrollMe')
], ChatComponent.prototype, "myScrollContainer", void 0);
ChatComponent = __decorate([
    Component({
        selector: 'app-chat',
        templateUrl: './chat.component.html',
        styleUrls: ['./chat.component.scss']
    })
], ChatComponent);
export { ChatComponent };
