import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class ChatService {
    constructor(HttpClient) {
        this.HttpClient = HttpClient;
        this.hostname = 'localhost';
        this.PORT = 5555;
    }
    async SendMessage(message) {
        const body = new FormData();
        body.append('message', message);
        console.log(message);
        const res = await this.HttpClient.post(`http://${this.hostname}:${5555}/message`, body).toPromise();
        return res;
    }
    async SendQuery(message) {
        const body = new FormData();
        body.append('message', message);
        console.log(message);
        const res = await this.HttpClient.post(`http://${this.hostname}:${5555}/query`, body).toPromise();
        return res;
    }
    startAudioBack() {
        const body = new FormData();
        body.append('message', JSON.stringify({ audio: true }));
        const res = this.HttpClient.post(`http://${this.hostname}:${5555}/`, body)
            .toPromise();
        console.log(res);
        return res;
    }
}
